import React from "react";
import "./dep_buttons.css";

const DepButtons = () => {
    const [activeIndex, setActiveIndex] = React.useState(0);

    const OnClickBtn = (index) => {
        setActiveIndex(index);
    };
    return (
        <div className="buttons_block">
            <div className="first_column">
                <button onClick={() => OnClickBtn(0)} className="long_button">
                    Логистика
                </button>
                <button className="short_button">Админ</button>
            </div>
            <div className="second_column">
                <button className="short_button">Офис</button>
                <button className="long_button">Производство</button>
            </div>
        </div>
    );
};

export default DepButtons;
