import React from 'react';
import './SwitchAdmin.css';

const SwitchAdmin = () => {
  const [activeIndex, setActiveIndex] = React.useState(0);

  const btns = ['Все', 'Добавить'];

  const OnClickBtn = (index) => {
    setActiveIndex(index);
  };
  return (
    <div class="switch_button">
      {/* <button
        onClick={() => OnClickBtn(0)}
        className={activeIndex === 0 ? 'active_btn' : 'left_button'}>
        Все
      </button>
      <button
        onClick={() => OnClickBtn(1)}
        className={activeIndex === 1 ? 'active_btn' : 'right_button'}>
        Добавить
      </button> */}

      {btns.map((value, i) => (
        <button
          onClick={() => OnClickBtn(i)}
          className={activeIndex === i ? 'active_btn' : ''}></button>
      ))}
    </div>
  );
};

export default SwitchAdmin;
