import React from "react";

import CategoryReport from "../components/CategoryReport";
import SwitchAdmin from "../components/SwitchAdminShop";
import AdminForm from "../components/AdminForm";
import Users from "../components/Users";

const AdminShop = () => {
    const [activeFrom, setActiveForm] = React.useState(0);
    return (
        <div className="root-adm sp">
            <div className="user-content">
                <div className="header admsp">
                    <CategoryReport />
                </div>
                <div className="switch">
                    <SwitchAdmin
                        value={activeFrom}
                        onChangeButtons={(i) => setActiveForm(i)}
                    />
                </div>
                {activeFrom ? (
                    <AdminForm />
                ) : (
                    <div className="user-container">
                        <h4>Пользователи</h4>
                        <Users />
                        <Users />
                        <Users />
                    </div>
                )}

                <div className="form__user">
                    <div className="form--content">
                        <input type="text" placeholder="ФИО" />
                        <input type="text" placeholder="Роль" />
                        <input type="text" placeholder="Номер телефона" />
                        <input type="text" placeholder="Надежный пароль" />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AdminShop;
